import React from "react";
import { useSelector } from "react-redux";

const SearchResult = () => {
  const searchResult = useSelector((state) => state.search.items);
  const accessToken = useSelector((state) => state.auth.token);
  const id = "ed48adfba9de53c3e5ed79657dbebe546e620bb4"; //todo get this from state
  console.log(searchResult);

  const addToQueue = async (item) => {
    console.log(item);
    const options = {
      method: "POST",
      headers: { Authorization: `Bearer ${accessToken}` },
    };

    const request = await fetch(
      `https://api.spotify.com/v1/me/player/queue?uri=${item}`,
      options
    );
    const response = await request.json();
    console.log({ addedToQueue: response });
  };

  if (!searchResult || searchResult === null) {
    return <p>loading</p>; // todo loading
  }

  return searchResult.map((result) => {
    return (
      <p>
        {result.name} - <span>{result.artists[0].name}-</span>
        <button onClick={() => addToQueue(result.uri)}>add</button>
      </p>
    );
  });
};

export default SearchResult;
