import React from "react";
import { useSelector } from "react-redux";

const SearchResult = () => {
  const searchResult = useSelector((state) => state.search.items);
  const accessToken = useSelector((state) => state.auth.token);
  const id = "ed48adfba9de53c3e5ed79657dbebe546e620bb4"; //tod get this from state

  const play = async () => {
    const options = {
      method: "PUT",
      headers: { Authorization: `Bearer ${accessToken}` },
    };

    const request = await fetch(
      `https://api.spotify.com/v1/me/player/play?device_id=${id}`,
      options
    );
  };

  if (!searchResult) {
    return null; // todo loading
  }

  searchResult.map((result) => {
    return (
      <p>
        {result.name} <span>{result.artists[0]}</span>
        <button>play</button>
      </p>
    );
  });
};

export default SearchResult;
