const initialState = {
  item: null,
  playing: false,
  status: "idle",
};

export default function queueReducer(state = initialState, action) {
  switch (action.type) {
    case "ADD_TO_QUEUE": {
      const newItem = [...state.item, action.data];
      return {
        ...state,
        item: newItem,
        status: "idle",
      };
    }
    default: {
      return state;
    }
  }
}
