const initialState = {
  item: null,
  playing: false,
  status: "idle",
};

export default function queueReducer(state = initialState, action) {
  switch (action.type) {
    case "ADD_TO_QUEUE": {
      return {
        ...state,
        item: [...item, action.data],
        status: "idle",
      };
    }
    default: {
      return state;
    }
  }
}
