const initialState = {
  type: null, //album, track, artist
  albumName: null,
  artistName: null,
  trackName: null,
  state: "idle",
};

export default function searchReducer(state = initialState, action) {
  switch (action.type) {
    case "SEARCH_REQUEST": {
      return {
        ...state,
        status: "loading",
      };
    }
    // case "RECEIVE_ALBUM": {
    //     return {
    //         ...state,
    //         type: album,
    //         albumName: ...action.data.name,
    //         status: "idle"
    //     }
    // }
    default: {
      return state;
    }
  }
}
