const initialState = {
  isAdmin: false,
  mainProfile: false,
  information: {},
  status: "idle",
};

export default function userReducer(state = initialState, action) {
  switch (action.type) {
    case "REQUEST_USER_TOKEN": {
      return {
        ...state,
        status: "loading",
      };
    }
    default: {
      return state;
    }
  }
}
